class a{constructor(t){this.element=t,this.items=[],this.init()}init(){this.element.querySelectorAll("[data-tab]").forEach(i=>{let e=i.dataset.tab;this.items[e]={button:i,content:document.querySelector(e),isOpen:this.isOpen(i)},i.addEventListener("click",()=>this.openTab(e))})}isOpen(t){return!!t.classList.contains("active")}openTab(t){if(!this.items[t].isOpen)for(const[e,s]of Object.entries(this.items))s.button.classList.toggle("active",e===t),s.content.classList.toggle("open",e===t),s.isOpen=e===t}}function c(n){new a(n)}export{c as default};
